import axios from "axios";

const baseic = axios.create({baseURL:`http://localhost:8080/users/`})

export const todoApi = (UserName)=> baseic.get(`${UserName}/todos`)

export const deltosoApi= (UserName,id)=> baseic.delete(`${UserName}/todos/${id}`)

export const getATodoApi = (UserName,id)=> baseic.get(`${UserName}/todos/${id}`)

export const updateTodoAPI= (UserName,id ,values)=>baseic.put(`${UserName}/todos/${id}`)

//http://localhost:8080/users/Saksham/todos/3
