import { useState } from 'react';
import {propTypes} from 'prop-types'
import './Counter.css';

export default function Counter({by}){
    
    Counter.propTypes={
        by: propTypes.number
    }

    //use state 1 array return krta hai with 1 value as a counter and 2 nd one as a updater
    //const state = useState(0);

    //isliye aaisa krke hm directly usko variaables me save kr skte hai
    const [count , setCount] = useState(0);
    
    function Increamentby1(){
        //state[1](state[0]+1);
        setCount(count+by);
    }
    
    function Decrementby1(){
        //state[1](state[0]+2);
        if(count-by>=0)
        setCount(count-by);
    }

    return(
        <div className="Counter">
            <span className="count">{count}</span>
            <div>
                <button className="Counterbutton" onClick={Increamentby1}>
                    +{by}
                </button>
            
                <button className="Counterbutton" onClick={Decrementby1}>
                    -{by}
                </button>
            </div>            
        </div>

    );

    

}
