import { useState } from 'react'
import { BrowserRouter , Routes , Route , useNavigate, useParams,Link } from 'react-router-dom';
import './TodoApp.css'

export default  function TodoApp(){
    return(
        <>
            <HeaderComponent/>
            <BrowserRouter>
            <Routes>
            <Route path='/' element={<LoginComponent/>}/>
                <Route path='/Login' element={<LoginComponent/>}/>
                <Route path='/Welcome/:UserName' element={< WelcomeComponent/>}/>
                <Route path='/Todo' element={<TodoComponent/>}/>
                <Route path='/Logout' element={<LogoutComponent/>}/>
                <Route path='*' element={< ErrorComponent/>}/>
            </Routes>
            </BrowserRouter>

            <FooterComponent/>
            
        </>

    )
}
function WelcomeComponent(){
    const {UserName}=useParams();
    return(
        <div className='WelcomeComponent'>
            <h1>Welcome {UserName}</h1>
            Your Todos : 

            <Link to='/Todo'>Manage your Todos</Link>
        </div>
    )
}
function ErrorComponent(){
    return(
        <div className='ErrorComponent'>
            <h1>We Are Working Really Hard!</h1>
            Apologies for the 404. Reach out to our team at 790-510-4347.
        </div>
    )
}
function LogoutComponent(){
    return(
        <div className='LogoutComponent'>
            <h1>You have been logged out</h1>
            Thank You for visiting.Please come back soon.
        </div>
    )
}
function TodoComponent(){
    const today = new Date();

    const todos = [
        {ID:1 , Description:'Learn AWS' , Done: false , TargetDate: (new Date(today.getFullYear()+2,today.getMonth(),today.getDay()))},
        {ID:2 , Description:'Learn Full Stack' , Done: false , TargetDate: (new Date(today.getFullYear()+3,today.getMonth(),today.getDay()))},
        {ID:3 , Description:'Learn DevOps',Done: false , TargetDate: (new Date(today.getFullYear()+5,today.getMonth(),today.getDay()))},
    ]
    return(
        <div className='container'>
            <h1>Things you want to do!</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <td>ID</td>
                        <td>Description</td>
                        <td>Done</td>
                        <td>Target Date</td>
                    </tr>
                </thead>
                <tbody>
                    {
                    todos.map(
                            todo => (
                                <tr key={todo.ID}>
                                    <td>{todo.ID}</td>
                                    <td>{todo.Description}</td>
                                    <td>{todo.Done.toString()}</td>
                                    <td>{todo.TargetDate.toDateString()}</td>
                                </tr>
                            )
                        )
                    }
                </tbody>

            </table>
        </div>
    )
}

function LoginComponent(){

    const [UserName,SetUserName]= useState("Saksham");
    const [password,SetPassword]= useState("");
    const [success,Setsuccess]= useState(false);
    const [failure,Setfailure]= useState(false);
    const navi = useNavigate();


    function UserChangeevent(event){
        SetUserName(event.target.value);
    }
    function PasswordChangeEvent(event){
        SetPassword(event.target.value);
    }
    function loginEventHndler(){
        if(UserName==="Saksham"&&password==="dummy"){
            Setsuccess(true);
            Setfailure(false);
            navi(`/Welcome/${UserName}`);
        }
        else{
            Setsuccess(false);
            Setfailure(true);
        }
    }

    return(
        <div className="LoginComponent">
            <h1>Please Login!</h1>
            {success && <div>Autentiction Successful</div>}
            {failure && <div>Autentiction Failed. Please check your credentials.</div>}
            <div className="LoginForm">
            <div>
                <label>User Name</label>
                <input type="text" name="UserName" value={UserName} onChange={UserChangeevent}/>
            </div>
            <div>
                <label>Password</label>
                <input type="password" name="password" value={password} onChange={PasswordChangeEvent}/>
            </div>
            <button type="button" onClick={loginEventHndler}>Login</button>
            </div>
        </div>
    )
}

function HeaderComponent(){
    return(
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
        </button>
        <a class="navbar-brand" href="#">Navbar</a>

        <div class="collapse navbar-collapse" id="navbarTogglerDemo03">
            <ul class="navbar-nav mr-auto mt-2 mt-lg-0">
            <li class="nav-item active">
                <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
            </li>
            <li class="nav-item">
                <a class="nav-link" href="#">Link</a>
            </li>
            <li class="nav-item">
                <a class="nav-link disabled" href="#">Disabled</a>
            </li>
            </ul>
            <ul class="navbar-nav">
                <li class="nav-item active">
                    <a class="nav-link" href="#">Logout<span class="sr-only"></span></a>
                </li>
            </ul>
        </div>
        </nav>
    )
}
function FooterComponent(){
    return(
        <footer className='footer'>
           <div className='container'>Footer</div>
        </footer>
    )
}
