import { useState } from 'react';
import './Counter.css';

export default function Counter(){

    //use state 1 array return krta hai with 1 value as a counter and 2 nd one as a updater
    //const state = useState(0);

    //isliye aaisa krke hm directly usko variaables me save kr skte hai
    const [count , setCount] = useState(0);
    
    function Increamentby1(){
        //state[1](state[0]+1);
        setCount(count+1);
    }
    
    function Decrementby1(){
        //state[1](state[0]+2);
        setCount(count-1);
    }
    function Increamentby2(){
        setCount(count+2);
    }
        
    function Decrementby2(){
        setCount(count-2);
    }
    function Increamentby5(){
        setCount(count+5);
    }
        
    function Decrementby5(){
        setCount(count-5);
    }
    function Reset(){
        setCount(0);
    }

    


    return(
        <div className="Counter">
            <span className="count">{count}</span>
            <div>
                <button className="Counterbutton" onClick={Increamentby1}>
                    +1
                </button>
            
                <button className="Counterbutton" onClick={Decrementby1}>
                    -1
                </button>
            </div> 
            <div>
                <button className="Counterbutton" onClick={Increamentby2}>
                    +2
                </button>
            
                <button className="Counterbutton" onClick={Decrementby2}>
                    -2
                </button>
            </div> 
            <div>
                <button className="Counterbutton" onClick={Increamentby5}>
                    +5
                </button>
            
                <button className="Counterbutton" onClick={Decrementby5}>
                    -5
                </button>
            </div>   
            <div>
                <button className="Counterresetbutton" onClick={Reset}>
                    RESET
                </button>
            </div>     
            
        </div>

    );
}
