import { useParams } from "react-router-dom"
import { useAuth } from "./Security/AuthContext"
import { getATodoApi, updateTodoAPI } from "./API/TodoAPI"
import { useEffect, useState } from "react"
import { ErrorMessage, Field, Form, Formik } from "formik"

export default function UpdateTodoComp(){

    const {id}=useParams()
    const[targetDate,setTargetDate]=useState("")
    const auth=useAuth()
    const [description , setDescription]=useState(null)

    useEffect(
        ()=>retriveAPI(),[id]
    )

    function submittedForm(values){
        const todos={
            id:id,
            username:auth.UserName,
            description:values.description,
            targetDate:values.targetDate,
            done:false
        }
        updateTodoAPI(auth.UserName,id,todos)
        .then((response)=>{console.log(response)})
        .catch((error)=>{console.log(error)})

    }

    function retriveAPI(){
        getATodoApi(auth.UserName,id)
            .then((response)=>{
                console.log(response.data)
                setDescription(response.data.description)
                setTargetDate(response.data.targetDate)
            })
            .catch((error)=>console.log(error))
    }

    function validation(value){
        let error = {
        }
        if(value.description.length<4){
            error.description='Enter minimum 5 characters'
        }
        if(value.targetDate == null){
            error.targetDate='Enter a valid date'
        }

        return error
    }

    return(
        <div className="container">
            <h1>Update Todo</h1>
            <div>
                <Formik  initialValues={{description,targetDate}}
                    enableReinitialize={true}
                    onSubmit={submittedForm}
                    validate={validation}
                    validateOnChange ={false}
                    validateOnBlur ={false}
                >{
                    (props)=>(
                        <Form>
                            <ErrorMessage 
                            name="description" 
                            component="div"
                            className="alert alert-warning"/>
                            <ErrorMessage 
                            name="targetDate" 
                            component="div"
                            className="alert alert-warning"/>
                            <fieldset className="form-group" >
                                <label>
                                    Description
                                </label>
                                <Field className="form-control" type="text" name="description"/>
                            </fieldset>
                            <fieldset className="form-group">
                                <label>
                                        Target Date
                                </label>
                                <Field className="form-control" type="date" name="targetDate" />
                            </fieldset> 
                            <div>
                                <button className="btn btn-success m-5" type="submit">Save</button>
                            </div>
                        </Form>
                    )
                }             
                </Formik>
            </div>
        </div>


    )

}
