import axios from "axios";

const baseic = axios.create({baseURL:`http://localhost:8080/users/`})

export const todoApi = (UserName,token)=> baseic.get(`${UserName}/todos`,{
    headers:{
        Authorization:token
    }

})

export const deltosoApi= (UserName,id,token)=> baseic.delete(`${UserName}/todos/${id}`,{
    headers:{
        Authorization:token
    }

})

export const getATodoApi = (UserName,id,token)=> baseic.get(`${UserName}/todos/${id}`,{
    headers:{
        Authorization:token
    }

})

export const updateTodoAPI= (UserName,id ,todos,token)=>baseic.put(`${UserName}/todos/${id}`,todos,{
    headers:{
        Authorization:token
    }

})

//http://localhost:8080/users/Saksham/todos/3


export const newtodoApi = (UserName,todos)=> baseic.post(`${UserName}/todos`,todos,{
    headers:{
        Authorization:token
    }

})
