import {Link } from 'react-router-dom';
import {  useAuth } from './Security/AuthContext';
import { todoApi } from './API/TodoAPI';
import TodoComponent from './TodoComponent';




export default function HeaderComponent(){

    const authcontext = useAuth();

    const isAuthenticated = authcontext.isAuthenticated

    function Logout(){
        authcontext.Logout()
    }


    function todoApii(){
        todoApi(authcontext.UserName)
            .then((response)=>succesfulres(response))
            .catch((error)=>errorResponse(error))
            .finally(()=>console.log('Cleanup'))
    }
    function succesfulres(response){
        const table = response.data;
        
        TodoComponent(table)
        
        console.log(table.map(todo=>(todo.id)))
    }
    function errorResponse(error){
        console.log(error)
    }

    //console.log(authcontext.number);

    return(
        <header className="border-bottom border-light border-5 mb-5 p-2">
            <div className="container">
                <div className="row">
                    <nav className="navbar navbar-expand-lg">
                        <Link className="navbar-brand ms-2 fs-2 fw-bold text-black" to="#">SG</Link>
                        <div className="collapse navbar-collapse">
                            <ul className="navbar-nav">
                                <li className="nav-item fs-5">
                                    {isAuthenticated && 
                                    <Link className="nav-link" to="/welcome/Saksham">Home</Link>}
                                </li>
                                <li className="nav-item fs-5">
                                    {isAuthenticated && 
                                    <Link className="nav-link" to="/Todo" onClick={todoApii}>Todos</Link>}
                                </li>
                            </ul>
                        </div>
                        <ul className="navbar-nav">
                            <li className="nav-item fs-5">
                                {!isAuthenticated && 
                                <Link className="nav-link" to="/login">Login</Link>}
                            </li>
                            <li className="nav-item fs-5">
                                {isAuthenticated && 
                                <Link className="nav-link" to="/logout" onClick={Logout}>Logout</Link>}
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        </header>
    )
}
