import { useState } from 'react';
import {PropTypes} from 'prop-types'
import './Counter.css';

export default function Counter(){

    return(
        <>
        <CounterButton />
        <CounterButton by={2}/>
        <CounterButton by={5}/>
        </>
    )
}


function CounterButton({by}){

    

    //use state 1 array return krta hai with 1 value as a coounter and 2 nd one as a updater
    //const state = useState(0);

    //isliye aaisa krke hm directly usko variaables me save kr skte hai
    const [count , setCount] = useState(0);
    
    function Increamentby1(){
        //state[1](state[0]+1);
        setCount(count+by);
    }
    
    function Decrementby1(){
        //state[1](state[0]+2);
        if(count-by>=0)
        setCount(count-by);
    }

   

    return(
        <div className="CounterButton">
            <span className="count">{count}</span>
            <div>
                <button className="Counterbutton" onClick={Increamentby1}>
                    +{by}
                </button>
            
                <button className="Counterbutton" onClick={Decrementby1}>
                    -{by}
                </button>
            </div>            
        </div>

    )
}

CounterButton.propTypes={
    by: PropTypes.number
}

//isse agr me by ki koi value pass ni krunga in app.jsx tb bhi by ki value 1 pass hogi automatically.
CounterButton.defaultProps={
    by: 1
}
