import { useState } from 'react';
import './Counter.css';

export default function Counter(){
    //const state = useState(0);

    const [count , setCount] = useState(0);
    
    function Increamentby1(){
    //state[1](state[0]+1);
    setCount(count+1);
    }

    function Increamentby2(){
        //state[1](state[0]+2);
        setCount(count+2);
        }


    return(
        <div className="Counter">
            <span className="count">{count}</span>
            <div>
                <button className="Counterbutton" onClick={Increamentby1}>
                    +1
                </button>
            
                <button className="Counterbutton" onClick={Increamentby2}>
                    +2
                </button>
            </div>        
            
        </div>

    );
}
